    @Test
    public void convertHexStream() {
        final String name = "sctpHeartBeat";
        final String stream = "10010f1c9398bb22f802a0ca040000340001003002000f1cc6182e3100000000000000000000ffffc6182e3100000000f30024c1020000007941ffa57907e657";
        assertThat(stream.length() % 2, CoreMatchers.is(0));

        final StringBuilder sb = new StringBuilder("    public static final byte[] " + name + " = new byte[] {\r\n");
        sb.append("        ");
        for (int i = 0; i < stream.length(); i += 2) {
            sb.append("(byte)0x" + stream.charAt(i) + stream.charAt(i + 1));
            if (i + 1 < stream.length() - 1) {
                sb.append(", ");
            }
            if (i % 16 == 14) {
                sb.append("\r\n");
                sb.append("        ");
            }
        }
        sb.append("};");
        System.out.println(sb.toString());
    }

